import { merge } from "@soltalabs/ramda-extra";

import handleNotFound from "./handleNotFound";
import handleOtherErrors from "./handleOtherErrors";

const defaultOpts = {
  badRequestErrors: ["ValidationError"],
};

export default (opts) => {
  const middlewareOpts = merge(defaultOpts, opts);

  return async (ctx, next) => {
    try {
      await next();
      if (ctx.status === 404) {
        handleNotFound(ctx);
      }
    } catch (err) {
      handleOtherErrors(err, ctx, middlewareOpts);
    }
  };
};
